mapper.select:
SELECT MARKET_NO AS marketNo ,USER_NICK AS userNick ,MARKET_TITLE AS marketTitle ,MARKET_TEXT AS marketText ,MARKET_ISDELETED AS marketIsDeleted ,MARKET_CREATE_AT AS marketCreateAt ,MARKET_CREATE_NAME AS marketCreateNm ,MARKET_PRICE AS marketPrice FROM ROYAL.TB_MARKET WHERE MARKET_ISDELETED != 'Y'
AND MARKET_NO = #{searchOne}
AND USER_NICK = #{searchTwo}

SELECT MARKET_NO AS marketNo ,USER_NICK AS userNick ,MARKET_TITLE AS marketTitle ,MARKET_TEXT AS marketText ,MARKET_ISDELETED AS marketIsDeleted ,MARKET_CREATE_AT AS marketCreateAt ,MARKET_CREATE_NAME AS marketCreateNm ,MARKET_PRICE AS marketPrice FROM ROYAL.TB_MARKET WHERE 1=1
AND MARKET_NO = ${marketNo}
ORDER BY MARKET_NO DESC

mapper.insert:
