mapper.select:
SELECT *
FROM ROYAL.TB_USER
WHERE USER_NICK=#{userNick} AND USER_PW=#{userPw}

mapper.insert:
INSERT INTO ROYAL.TB_USER (
	USER_NICK
	,USER_EMAIL
	,USER_PW
	,USER_NAME
	,USER_ADD
	,USER_PHONE

		, USER_ISDELETED

	, USER_CREATE_AT
	, USER_CREATE_NAME
	,USER_ROLE
)VALUES(
	#{userNick}
	,#{userEmail}
	,#{userPw}
	,#{userNm}
	,#{userAdd}
	,#{userPhone}

		,#{userIsdeleted}

	, TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS')
	, 'USER'
	,'USER'
)

mapper.update:
UPDATE ROYAL.TB_USER SET 
	USER_MODIFY_AT = TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS')
	, USER_MODIFY_NAME = 'USER'

		,USER_PW = #{userPw}


		,USER_NAME = #{userNm}


		,USER_ADD = #{userAdd}


		,USER_PHONE = #{userPhone}


		,USER_EMAIL = #{userEmail}

WHERE USER_NICK = #{userNick}

mapper.select:
SELECT *
FROM ROYAL.TB_USER
WHERE USER_EMAIL = #{userEmail} AND USER_PHONE = #{userPhone}
